#include <stdio.h>
#include <string.h>

void main()
{
	char input[] = "\\\\";
	char pad[3][3] = {{'1', '2', '3'}, {'4', '5', '6'}, {'7', '8', '9'}};
	char code[255];
	int pos = 0;
	int row = 1;
	int column = 1;

	for (int i = 0; i < strlen(input) + 1; i++) {
		switch (input[i]) {
			case '\n':
			case '\0':
				code[pos] = pad[row][column];
				pos++;
				break;
			case 'U':
				row--;
				break;
			case 'D':
				row++;
				break;
			case 'L':
				column--;
				break;
			case 'R':
				column++;
				break;
		}
		if (row < 0) row = 0;
		if (row > 2) row = 2;
		if (column < 0) column = 0;
		if (column > 2) column = 2;
	}

	printf("%s\n", code);
}
